                const Icon = category.icon;
                const isSelected = selectedCategory === category.id;

                return (
                  <Card
                    key={category.id}
                    className={`cursor-pointer transition-all hover-elevate active-elevate-2 ${
                      isSelected ? 'border-primary border-2 bg-primary/5' : ''
