import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import ctwLogoUrl from "@assets/CTWI-logo_1763155720403.png";

const loginSchema = z.object({
  username: z.string().min(1, "Username is required"),
  password: z.string().min(1, "Password is required"),
});

type LoginFormData = z.infer<typeof loginSchema>;

export default function Login() {
  const { toast } = useToast();
  const [error, setError] = useState<string | null>(null);

  const form = useForm<LoginFormData>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      username: "",
      password: "",
    },
  });

  const loginMutation = useMutation({
    mutationFn: async (data: LoginFormData) => {
      const res = await apiRequest("POST", "/api/login", data);
      const response = await res.json() as { success: boolean; username?: string; message?: string };
      if (!response.success) {
        throw new Error(response.message || "Access Denied");
      }
      return response;
    },
    onSuccess: async () => {
      await queryClient.invalidateQueries({ queryKey: ['/api/check-auth'] });
      await queryClient.refetchQueries({ queryKey: ['/api/check-auth'] });
      toast({
        title: "Login successful",
        description: "Welcome back!",
      });
    },
    onError: (error: Error) => {
      setError(error.message);
      toast({
        title: "Login failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: LoginFormData) => {
    setError(null);
    loginMutation.mutate(data);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-background to-muted p-4">
      <Card className="w-full max-w-md">
        <CardHeader className="space-y-4 text-center">
          <div className="flex justify-center">
            <img src={ctwLogoUrl} alt="CTWI Logo" className="h-16 w-auto" />
          </div>
          <CardTitle className="text-2xl">DC/MD Union Trades Pathway</CardTitle>
          <CardDescription>Sign in to access the portal</CardDescription>
        </CardHeader>
        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="username"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Username</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="Enter your username"
                        {...field}
                        data-testid="input-username"
                        autoComplete="username"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="password"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Password</FormLabel>
                    <FormControl>
                      <Input
                        type="password"
                        placeholder="Enter your password"
                        {...field}
                        data-testid="input-password"
                        autoComplete="current-password"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              {error && (
                <div className="text-sm text-destructive text-center p-3 bg-destructive/10 rounded-md" data-testid="text-error">
                  {error}
                </div>
              )}

              <Button
                type="submit"
                className="w-full"
                disabled={loginMutation.isPending}
                data-testid="button-login"
              >
                {loginMutation.isPending ? "Signing in..." : "Sign In"}
              </Button>
            </form>
          </Form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/contexts/AuthContext.tsx.content:
import { createContext, useContext, ReactNode } from "react";
import { useQuery } from "@tanstack/react-query";

interface AuthResponse {
  authenticated: boolean;
  username?: string;
  login_time?: string;
}

type AuthStatus = 'loading' | 'authenticated' | 'unauthenticated' | 'error';

interface AuthContextValue {
  auth: AuthResponse | undefined;
  status: AuthStatus;
  isAuthenticated: boolean;
  isLoading: boolean;
}

const AuthContext = createContext<AuthContextValue | undefined>(undefined);

export function AuthProvider({ children }: { children: ReactNode }) {
  const { data: auth, isLoading, isFetching, error } = useQuery<AuthResponse>({
    queryKey: ['/api/check-auth'],
    staleTime: 0,
    refetchOnMount: 'always',
    refetchOnWindowFocus: true,
    retry: false,
  });

  let status: AuthStatus;
  if (isLoading || isFetching) {
    status = 'loading';
  } else if (error) {
    status = 'error';
  } else if (auth?.authenticated) {
    status = 'authenticated';
  } else {
    status = 'unauthenticated';
  }

  const value: AuthContextValue = {
    auth,
    status,
    isAuthenticated: status === 'authenticated',
    isLoading: status === 'loading',
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within AuthProvider');
  }
  return context;
}

server/utils/csvLogger.ts.content:
import fs from 'fs';
import path from 'path';
import { stringify } from 'csv-stringify/sync';

const LOG_FILE = 'activity_log.csv';
const HEADER = ['timestamp', 'username', 'page', 'details'];

export function logToCSV(username: string, page: string, details: string): void {
  const logFilePath = path.join(process.cwd(), LOG_FILE);

  if (!fs.existsSync(logFilePath)) {
    const headerRow = stringify([HEADER]);
    fs.writeFileSync(logFilePath, headerRow, 'utf8');
  }

  const timestamp = new Date().toISOString();
  const row = [timestamp, username, page, details];
  const csvRow = stringify([row]);

  fs.appendFileSync(logFilePath, csvRow, 'utf8');
}

